    name: "Farhan Siddiqui",
    role: "Marketing Director",
    avatar: window.BazaarAvatars['asian-woman']
  }
];

const script_8a7f2b3c = [
  { type: "header", frames: 360 },
  { type: "testimonials", frames: 360 }
];

let accumulatedFrames_8a7f2b3c = 0;
const sequences_8a7f2b3c = script_8a7f2b3c.map(item => {
  const sequence = { ...item, start: accumulatedFrames_8a7f2b3c };
  accumulatedFrames_8a7f2b3c += item.frames;
  return sequence;
});

const totalFrames_8a7f2b3c = script_8a7f2b3c.reduce((sum, s) => sum + s.frames, 0);
export const durationInFrames_8a7f2b3c = totalFrames_8a7f2b3c;

const TestimonialCard = ({ testimonial, delay, speed }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  const cardSpring = spring({
    frame: frame - delay,
    fps,
    config: { damping: 12, stiffness: 100 }
  });

  const translateY = interpolate(
    frame,
    [0, 600],
    [0, -2000],
    { extrapolateLeft: "clamp", extrapolateRight: "extend" }
  ) * speed;

  const opacity = interpolate(cardSpring, [0, 1], [0, 1], { extrapolateLeft: "clamp", extrapolateRight: "clamp" });
  const scale = interpolate(cardSpring, [0, 1], [0.8, 1], { extrapolateLeft: "clamp", extrapolateRight: "clamp" });

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "24px",
        padding: "40px",
        marginBottom: "24px",
        maxWidth: "320px",
        width: "100%",
        boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
        border: "1px solid rgba(0,0,0,0.05)",
        opacity,
        transform: `translateY(${translateY}px) scale(${scale})`,
        transformOrigin: "center"
      }}
    >
      <div
        style={{
          fontSize: "16px",
          lineHeight: "1.5",
          color: "#374151",
          marginBottom: "20px",
          fontFamily: "Inter",
          fontWeight: "400"
        }}
      >
        {testimonial.text}
      </div>
      <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
        <img
          src={testimonial.avatar}
          style={{
            width: "40px",
            height: "40px",
            borderRadius: "50%",
            objectFit: "cover"
          }}
        />
        <div>
          <div
            style={{
              fontFamily: "Inter",
              fontWeight: "600",
              fontSize: "14px",
              color: "#111827",
              lineHeight: "1.2"
            }}
          >
            {testimonial.name}
          </div>
          <div
            style={{
              fontFamily: "Inter",
              fontWeight: "400",
              fontSize: "14px",
              color: "#6B7280",
              lineHeight: "1.2"
            }}
          >
            {testimonial.role}
          </div>
        </div>
      </div>
    </div>
  );
};

const TestimonialColumn = ({ testimonials, speed, delay }) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
      {[...testimonials, ...testimonials, ...testimonials].map((testimonial, index) => (
        <TestimonialCard
          key={index}
          testimonial={testimonial}
          delay={delay}
          speed={speed}
        />
      ))}
    </div>
  );
};

export default function TemplateScene() {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  const headerSequence = sequences_8a7f2b3c.find(s => s.type === "header");
  const testimonialsSequence = sequences_8a7f2b3c.find(s => s.type === "testimonials");

  return (
    <AbsoluteFill
      style={{
        background: "linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)",
        fontFamily: "Inter"
      }}
    >
      {/* Header Section */}
      {frame >= headerSequence.start && frame < headerSequence.start + headerSequence.frames && (
        <div
          style={{
            position: "absolute",
            top: "80px",
            left: "50%",
            transform: "translateX(-50%)",
            textAlign: "center",
            zIndex: 10
          }}
        >
          <h1
            style={{
              fontSize: "48px",
              fontWeight: "800",
              color: "#111827",
              marginBottom: "16px",
              fontFamily: "Inter",
              opacity: interpolate(
                spring({
                  frame: frame - headerSequence.start,
                  fps,
                  config: { damping: 12, stiffness: 100 }
                }),
                [0, 1],
                [0, 1],
                { extrapolateLeft: "clamp", extrapolateRight: "clamp" }
              ),
              transform: `translateY(${interpolate(
                spring({
                  frame: frame - headerSequence.start,
                  fps,
                  config: { damping: 12, stiffness: 100 }
                }),
                [0, 1],
                [30, 0],
                { extrapolateLeft: "clamp", extrapolateRight: "clamp" }
              )}px)`
            }}
          >
            What our users say
          </h1>
        </div>
      )}

      {/* Testimonials Columns */}
      {frame >= headerSequence.start && (
        <div
          style={{
            position: "absolute",
            top: "200px",
            left: "0",
            right: "0",
            display: "flex",
            justifyContent: "center",
            gap: "32px",
            overflow: "hidden",
            height: "calc(100vh - 200px)"
          }}
        >
          <TestimonialColumn
            testimonials={testimonials_8a7f2b3c.slice(0, 2)}
            speed={0.8}
            delay={headerSequence.start}
          />
          <TestimonialColumn
            testimonials={testimonials_8a7f2b3c.slice(2, 4)}
            speed={1.0}
            delay={headerSequence.start}
          />
          <TestimonialColumn
            testimonials={testimonials_8a7f2b3c.slice(4, 6)}
            speed={1.2}
            delay={headerSequence.start}
          />
        </div>
      )}
    </AbsoluteFill>
  );
}

const { AbsoluteFill, Sequence, interpolate, useCurrentFrame, useVideoConfig, Img } = window.Remotion;

const script_meh0uew7 = [
  { type: "typing-text", frames: 60 },
  { type: "delay", frames: 5 },
  { type: "outro", frames: 15 },
  { type: "ui-slide", frames: 20 },
  { type: "new-text", frames: 60 },
  { type: "ui-transform", frames: 60 },
  { type: "background-transition", frames: 5 }
];

const sequences_meh0uew7 = [];
let accumulatedFrames_meh0uew7 = 0;

script_meh0uew7.forEach((item, index) => {
  sequences_meh0uew7.push({
    ...item,
    start: accumulatedFrames_meh0uew7,
    end: accumulatedFrames_meh0uew7 + item.frames
  });
  accumulatedFrames_meh0uew7 += item.frames;
});

const totalFrames_meh0uew7 = script_meh0uew7.reduce((sum, item) => sum + item.frames, 0);
export const durationInFrames_meh0uew7 = totalFrames_meh0uew7;

export default function TemplateScene() {
  window.RemotionGoogleFonts.loadFont("Poppins", { weights: ["500", "600", "800"] });

  const frame = useCurrentFrame();
  const { width, height } = useVideoConfig();

  // Background zoom out and rotation animation
  const backgroundScale = interpolate(
    frame,
    [0, 90],
    [3, 2],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const backgroundRotation = interpolate(
    frame,
    [0, 150, 210],
    [0, 180, 360],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Background fade transition (last 5 frames: 215-220)
  const oldBackgroundOpacity = interpolate(
    frame,
    [210, 220],
    [1, 0],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const newBackgroundOpacity = interpolate(
    frame,
    [210, 220],
    [0, 1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Text entrance animation (frames 0-30) and outro upward movement (frames 65-80) - SLOWER
  const textY = interpolate(
    frame,
    [0, 30, 65, 80],
    [height * 0.55, height * 0.5, height * 0.5, height * 0.2],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const textOpacity = interpolate(
    frame,
    [0, 30, 65, 80],
    [0, 1, 1, 0],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Text entrance scale (frames 0-20)
  const entranceScale = interpolate(
    frame,
    [0, 20],
    [1.3, 1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Text outro scale - zoom out (frames 65-80) - SLOWER
  const outroScale = interpolate(
    frame,
    [65, 80],
    [1, 0.3],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Combine entrance and outro scales
  const textScale = frame < 65 ? entranceScale : outroScale;

  // UI Image slide in animation (starts at frame 65, earlier timing)
  const uiImageY = interpolate(
    frame,
    [65, 85, 145, 205],
    [height + 100, height * 0.6, height * 0.5, height * 0.1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const uiImageOpacity = interpolate(
    frame,
    [65, 80],
    [0, 1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // UI Image scale animation - starts large and scales down while sliding in, then scales up to screen after delay, then scales up more in final 2 seconds
  const uiImageScale = interpolate(
    frame,
    [65, 85, 110, 120, 145, 205, 210],
    [1.5, 1, 1, 2.2, 2.2, 3.5, 0],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // UI Image perspective Y rotation to right side - EXTENDED: Now covers entire animation duration including new 2 seconds
  const uiImageRotateY = interpolate(
    frame,
    [85, 145, 205, 210],
    [0, 15, 35, 45],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // New text animation (starts at frame 65, now disappears later at frame 105-110 to give more time)
  const newTextY = interpolate(
    frame,
    [65, 110],
    [height * 0.22, height * 0.20],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const newTextOpacity = interpolate(
    frame,
    [65, 85, 115, 120],
    [0, 1, 1, 0],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const newTextScale = interpolate(
    frame,
    [65, 85],
    [1.3, 1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Mouse cursor animation - appears in last 2 seconds (frames 145-205)
  const mouseX = interpolate(
    frame,
    [145, 185],
    [width + 50, width * 0.30],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const mouseY = interpolate(
    frame,
    [145, 185],
    [height * 0.5, height * 0.93],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  const mouseOpacity = interpolate(
    frame,
    [145, 155, 195, 215],
    [0, 1, 1, 1],
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  // Enhanced click animation - scale down first, then scale up for more dramatic click effect
  const clickScale = interpolate(
    frame,
    [190, 202, 203, 208],
    [1.2, 0.8, 1.5, 1.2], // Increased size overall, scale down then up
    {
      extrapolateLeft: "clamp",
      extrapolateRight: "clamp"
    }
  );

  return (
    <AbsoluteFill style= {{ backgroundColor: "#1e40af" }
}>
  {/* Old background image with zoom out and rotation - fades out in last 5 frames */ }
  < Img
src = "https://i.postimg.cc/Wz519hX2/Untitled-1kjn.png"
style = {{
  position: "absolute",
    top: "50%",
      left: "50%",
        width: width * 1.2,
          height: height * 1.2,
            objectFit: "cover",
              transform: `translate(-50%, -50%) scale(${backgroundScale}) rotate(${backgroundRotation}deg)`,
                transformOrigin: "center center",
                  opacity: oldBackgroundOpacity
}}
/>

{/* New background image - fades in during last 5 frames with initial scale of 2 */ }
{
  frame >= 215 && (
    <Img
src="https://i.postimg.cc/XvQGvww9/Untitled-1kjn.png"
  style = {{
    position: "absolute",
      top: "50%",
        left: "50%",
          width: width * 1.2,
            height: height * 1.2,
              objectFit: "cover",
                transform: `translate(-50%, -50%) scale(2)`,
                  transformOrigin: "center center",
                    opacity: newBackgroundOpacity
  }
}
/>
)}

{/* Text with entrance and outro animations */ }
{
  frame >= 0 && frame <= 80 && (
    <div style={
      {
        position: "absolute",
          top: textY,
            left: "50%",
              transform: `translate(-50%, -50%) scale(${textScale})`,
                fontFamily: "Poppins",
                  fontSize: "6vw",
                    fontWeight: "800",
                      textAlign: "center",
                        lineHeight: "1.2",
                          letterSpacing: "0.05em",
                            color: "white",
                              width: "100%",
                                opacity: textOpacity,
                                  textShadow: "2px 2px 4px rgba(0,0,0,0.8)",
}
  }>
    Your Secure < br /> AI Meeting Assistant
      < /div>
)
}

{/* UI Image with slide in animation, scale down animation, border gradient, glow, and perspective depth + Y rotation */ }
{
  frame >= 65 && (
    <div style={
      {
        position: "absolute",
          top: uiImageY,
            left: "50%",
              transform: `translate(-50%, -50%) scale(${uiImageScale}) perspective(1000px) rotateX(15deg) rotateY(${uiImageRotateY}deg)`,
                opacity: uiImageOpacity,
                  padding: "4px",
                    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                      borderRadius: "16px",
                        boxShadow: "0 0 40px rgba(102, 126, 234, 0.6), 0 0 80px rgba(118, 75, 162, 0.4), 0 20px 40px rgba(0,0,0,0.3)",
}
  }>
    <Img
src="https://i.postimg.cc/WzNzVQ3b/a-LRGZ7s0-Y16-Icja-J88v-XIa3dc.png"
  style = {{
    width: width * 0.7,
      height: "auto",
        borderRadius: "12px",
          display: "block"
  }
}
/>
  < /div>
)}

{/* New text after UI image appears (now disappears later to give more time on screen) */ }
{
  frame >= 65 && frame <= 110 && (
    <div style={
      {
        position: "absolute",
          top: newTextY,
            left: "50%",
              transform: `translate(-50%, -50%) scale(${newTextScale})`,
                fontFamily: "Poppins",
                  fontSize: "4vw",
                    fontWeight: "600",
                      textAlign: "center",
                        color: "white",
                          width: "100%",
                            opacity: newTextOpacity,
                              textShadow: "2px 2px 4px rgba(0,0,0,0.8)",
}
  }>
    Record, transcribe, and summarize meetings
      < /div>
)
}

{/* Mouse cursor animation - appears in last 2 seconds with enhanced click effect */ }
{
  frame >= 145 && (
    <div style={
      {
        position: "absolute",
          left: mouseX,
            top: mouseY,
              transform: `translate(-50%, -50%) scale(${clickScale})`,
                opacity: mouseOpacity,
                  pointerEvents: "none",
                    zIndex: 1000
      }
  }>
    {/* Mouse cursor using lsiconðŸ˜›ointer-filled icon - solid white */ }
    < window.IconifyIcon
  icon = "lsiconðŸ˜›ointer-filled"
  style = {{
    fontSize: "60px",
      color: "#7d5beb",
        filter: "drop-shadow(3px 3px 8px rgba(0,0,0,0.6)) drop-shadow(0px 0px 4px rgba(30,58,138,0.4))"
  }
}
/>

{/* Click ripple effect */ }
{
  frame >= 190 && frame <= 200 && (
    <div style={
      {
        position: "absolute",
          top: "50%",
            left: "50%",
              width: "80px",
                height: "80px",
                  borderRadius: "50%",
                    border: "2px solid rgba(255,255,255,0.8)",
                      transform: `translate(-50%, -50%) scale(${interpolate(frame, [200, 210], [0.5, 2])})`,
                        opacity: interpolate(frame, [200, 210], [1, 0])
      }
  } />
)
}
</div>
)}
</AbsoluteFill>
);
}

