    };

    return (
        <AbsoluteFill
            style={{
                backgroundColor: 'black',
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <div style={textStyle}>Generating...</div>
        </AbsoluteFill>
    );
};

export const durationInFrames_me462q72 = 90;

