const { AbsoluteFill, useCurrentFrame, interpolate, useVideoConfig } = window.Remotion;

const script_mf165pbn = [
  { text: "Turn screenshots into demo videos", frames: 120 }
];

const sequences_mf165pbn = [];
let accumulatedFrames_mf165pbn = 0;

script_mf165pbn.forEach((item, index) => {
  sequences_mf165pbn.push({
    start: accumulatedFrames_mf165pbn,
    duration: item.frames,
    text: item.text
  });
  accumulatedFrames_mf165pbn += item.frames;
});

const totalFrames_mf165pbn = script_mf165pbn.reduce((sum, s) => sum + s.frames, 0);
export const durationInFrames_mf165pbn = totalFrames_mf165pbn;

export default function TemplateScene() {
  const frame = useCurrentFrame();
  const { width, height } = useVideoConfig();

  const words = ["Turn", "screenshots", "into", "demo", "videos"];
  const animationFrames = 8;
  const framesPerWord = 12;

  // Screenshot animation timing - starts when "screenshots" appears
  const screenshotWordIndex = 1;
  const screenshotStartFrame = screenshotWordIndex * framesPerWord;
  const screenshotAnimationDuration = 60;

  // Calculate word dimensions for proper sizing
  const fontSize = Math.min(width * 0.08, 80);
  const approximateWordWidth = fontSize * 6; // Approximate width for "screenshots"
  const approximateWordHeight = fontSize;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: "white",
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "row",
        gap: "20px",
        flexWrap: "wrap",
        padding: "40px"
      }}
    >
      {words.map((word, index) => {
        const startFrame = index * framesPerWord;
        const endFrame = startFrame + animationFrames;

        const opacity = interpolate(
          frame,
          [startFrame, endFrame],
          [0, 1],
          {
            extrapolateLeft: "clamp",
            extrapolateRight: "clamp"
          }
        );

        const translateY = interpolate(
          frame,
          [startFrame, endFrame],
          [30, 0],
          {
            extrapolateLeft: "clamp",
            extrapolateRight: "clamp"
