const FastTypewriterText = ({ text, delay, duration }) => {
  const frame = useCurrentFrame();
  const { width } = useVideoConfig();

  const chars = text.split('');
  const framesPerChar = duration / chars.length;

  const visibleChars = Math.min(
    chars.length,
    Math.max(0, Math.floor((frame - delay) / framesPerChar))
  );

  const displayText = chars.slice(0, visibleChars).join('');

  return (
    <div
      style={{
        fontSize: Math.min(width * 0.08, 120) + "px",
        fontWeight: "700",
        color: "#111827",
        fontFamily: "Inter",
        textAlign: "center",
        opacity: frame >= delay ? 1 : 0,
        lineHeight: "1.2",
        maxWidth: "95%"
      }}
    >
      {displayText}
      {frame >= delay && frame < delay + duration && visibleChars < chars.length && (
        <span style={{ opacity: 0.5, animation: "blink 1s infinite" }}>|</span>
      )}
    </div>
  );
};

