  const padding = (isPortrait ? width * 0.04 : width * 0.025) * 0.75;
  const borderRadius = width * 0.026 * 0.75;

  // Static text that always shows - with proper spacing
  const staticText = "Ask Lovable to create a";

  // Animation timing for typewriter effect with typing out
  const cycleFrames = 90; // Total frames per option (increased to accommodate typing out)
  const typeInFrames = 30; // Frames to type in each option
  const pauseFrames = 30; // Frames to pause with full text
  const typeOutFrames = 30; // Frames to type out each option

  // Calculate which option we're currently showing
  const currentCycle = Math.floor((frame - 30) / cycleFrames);
  const frameInCycle = (frame - 30) % cycleFrames;
  const optionIndex = currentCycle % typewriterOptions.length;
  const currentOption = typewriterOptions[optionIndex];

  // Calculate typewriter progress for current option
  let typewriterText = "";
  if (frame >= 30) {
    if (frameInCycle < typeInFrames) {
      // Typing in phase
      const charCount = Math.floor(
        interpolate(
          frameInCycle,
          [0, typeInFrames],
          [0, currentOption.length],
          { extrapolateLeft: "clamp", extrapolateRight: "clamp" }
        )
      );
      typewriterText = currentOption.slice(0, charCount);
    } else if (frameInCycle < typeInFrames + pauseFrames) {
      // Pause phase - show full text
      typewriterText = currentOption;
    } else {
      // Typing out phase
      const typeOutProgress = frameInCycle - typeInFrames - pauseFrames;
      const charCount = Math.floor(
        interpolate(
          typeOutProgress,
          [0, typeOutFrames],
          [currentOption.length, 0],
