export default function TemplateScene() {
  const frame = useCurrentFrame();

  const words = ["My", "name", "is", "Jeff"];
  const animationFrames = 4; // Faster animation per word
  const pauseFrames = 10; // Pause on final word
  const totalAnimationTime = 30 - pauseFrames; // 20 frames for all animations
  const framesPerWord = totalAnimationTime / words.length; // 5 frames per word

  return (
    <AbsoluteFill
      style={{
        backgroundColor: "white",
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        flexDirection: "row",
        gap: "20px"
      }}
    >
      {words.map((word, index) => {
        const startFrame = index * framesPerWord;
        const endFrame = startFrame + animationFrames;

        const opacity = interpolate(
          frame,
          [startFrame, endFrame],
          [0, 1],
          {
            extrapolateLeft: "clamp",
            extrapolateRight: "clamp"
          }
        );

        const translateY = interpolate(
          frame,
          [startFrame, endFrame],
          [30, 0],
          {
            extrapolateLeft: "clamp",
            extrapolateRight: "clamp"
          }
        );

        return (
          <div
            key={index}
            style={{
              fontSize: "80px",
              fontFamily: "Inter",
              fontWeight: "700",
              color: "black",
              opacity: opacity,
              transform: `translateY(${translateY}px)`,
            }}
          >
            {word}
          </div>
        );
      })}
    </AbsoluteFill>
  );
}

