const { AbsoluteFill, useCurrentFrame, useVideoConfig, interpolate } = window.Remotion;

const TemplateScene = () => {
  const frame = useCurrentFrame();
  const { width, height, fps } = useVideoConfig();

  // 2 seconds wave animation (60 frames at 30fps)
  const waveProgress = interpolate(frame, [0, 60], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
    easing: (t) => t // Linear easing for constant speed
  });

  const createWaveDelay = (x, y) => {
    const normalizedX = x / width;
    const normalizedY = y / height;
    const distance = Math.sqrt(normalizedX * normalizedX + (1 - normalizedY) * (1 - normalizedY));
    return distance * 0.4; // Reduced delay multiplier to ensure all elements animate within timeframe
  };

  const renderPlusPattern = () => {
    const elements = [];
    const spacing = 40;
    const cols = Math.ceil(width / spacing) + 2;
    const rows = Math.ceil(height / spacing) + 2;

    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const x = col * spacing - spacing;
        const y = row * spacing - spacing;

        const delay = createWaveDelay(x, y);
        // Fixed animation window - each element gets consistent 0.3 duration
        const elementProgress = interpolate(waveProgress, [delay, delay + 0.3], [0, 1], {
          extrapolateLeft: "clamp",
          extrapolateRight: "clamp"
        });

        let hue, opacity, scale;

        if (frame < 50) {
          // Pure wave animation phase
          hue = interpolate(elementProgress, [0, 1], [60, 240]);
          opacity = interpolate(elementProgress, [0, 1], [0.3, 1]);
          scale = interpolate(elementProgress, [0, 1], [0.5, 1]);
        } else if (frame < 70) {
          // Smooth transition phase (20 frames)
          const transitionProgress = interpolate(frame, [50, 70], [0, 1], {
            extrapolateLeft: "clamp",
            extrapolateRight: "clamp",
            easing: (t) => t * t * (3 - 2 * t) // Smooth S-curve easing
          });

          // Wave animation colors
          const waveHue = interpolate(elementProgress, [0, 1], [60, 240]);

          // Gradient colors based on position
          const normalizedX = (x + spacing) / (width + spacing * 2);
          const gradientHue = interpolate(normalizedX, [0, 1], [60, 240]);

          // Blend between wave and gradient colors
          hue = interpolate(transitionProgress, [0, 1], [waveHue, gradientHue]);
          opacity = interpolate(elementProgress, [0, 1], [0.3, 1]);
          scale = interpolate(elementProgress, [0, 1], [0.5, 1]);
        } else {
          // Final phase - pure gradient based on x position
          const normalizedX = (x + spacing) / (width + spacing * 2);
          hue = interpolate(normalizedX, [0, 1], [60, 240]); // Yellow-green to blue gradient
          opacity = 1;
          scale = 1;
        }

        elements.push(
          <div
            key={`${row}-${col}`}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              fontSize: "40px",
              fontWeight: "bold",
              color: `hsl(${hue}, 70%, 60%)`,
              opacity: opacity,
              transform: `scale(${scale})`,
              transformOrigin: "center",
              fontFamily: "monospace"
            }}
          >
            +
          </div>
        );
      }
    }
    return elements;
  };

  const statusProgress = interpolate(frame, [50, 60], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp"
  });

  const statusOpacity = interpolate(statusProgress, [0, 1], [0, 1]);

  // TBPN logo animation - appears after "watching" text
  const logoProgress = interpolate(frame, [36, 66], [0, 1], {
    extrapolateLeft: "clamp",
