const TemplateScene = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Card entrance animation
  const cardScale = spring({ frame, fps,
    config: {
      damping: 12,
      stiffness: 100,
      mass: 0.5,
    },
  });

  const cardOpacity = interpolate(frame, [0, 20], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Growth percentage animation
  const growthProgress = interpolate(frame, [20, 80], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const currentGrowth = interpolate(growthProgress, [0, 1], [0, 24.15], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Yellow line animation with slow in slow out easing - starts from first frame
  const lineProgressRaw = interpolate(frame, [0, 120], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Apply slow in slow out easing (ease-in-out cubic)
  const lineProgress = lineProgressRaw < 0.5 
    ? 2 * lineProgressRaw * lineProgressRaw 
    : 1 - Math.pow(-2 * lineProgressRaw + 2, 2) / 2;

  // Chart bars animation - visible from start
  const barsOpacity = 1;

  // Button animation - visible from start
  const buttonOpacity = 1;
  const buttonScale = 1;

  // Chart data with custom height sequence - removed final drop, ends at steady level
  const chartData = [
    0.3, 0.32, 0.35, 0.38, 0.4, 0.38, 0.35, 0.32, 0.3, 0.32, 0.35, 0.4, 0.45, 0.5, 0.55, 0.6, 0.58, 0.55, 0.5, 0.45,
    0.42, 0.4, 0.38, 0.36, 0.34, 0.32, 0.3, 0.32, 0.35, 0.38, 0.42, 0.46, 0.5, 0.54, 0.58, 0.6, 0.6
  ];

  // Enhanced smooth interpolation with better curve flattening
  const getSmoothedValue = (progress) => {
    const totalPoints = chartData.length - 1;
    const exactIndex = progress * totalPoints;
    const lowerIndex = Math.floor(exactIndex);
    const upperIndex = Math.min(lowerIndex + 1, totalPoints);
    const fraction = exactIndex - lowerIndex;

    // Enhanced smoothstep for flatter peaks and smoother curves
    const easedFraction = fraction * fraction * fraction * (fraction * (fraction * 6 - 15) + 10); // smootherstep

    const lowerValue = chartData[lowerIndex] || 0;
    const upperValue = chartData[upperIndex] || 0;

    return lowerValue + (upperValue - lowerValue) * easedFraction;
  };

  // Calculate current smoothed value based on line position - updated range to 15-40%
  const currentValue = getSmoothedValue(lineProgress);
  const displayValue = interpolate(currentValue, [0, 1], [15, 40], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill style={{ backgroundColor: "#f0f0f0" }}>
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: `translate(-50%, -50%) scale(${cardScale})`,
          opacity: cardOpacity,
          width: "400px",
