              const staggerDelay = Math.floor(index / 5) * 1;
              const iconOpacity = interpolate(
                frame,
                [15 + staggerDelay, 18 + staggerDelay],
                [0, 1],
                {
                  extrapolateLeft: "clamp",
                  extrapolateRight: "clamp"
                }
              );

              const iconScale = interpolate(
                frame,
                [15 + staggerDelay, 18 + staggerDelay],
                [0.5, 1],
                {
                  extrapolateLeft: "clamp",
                  extrapolateRight: "clamp"
                }
              );

              // Calculate column position for depth effect
              const col = index % 5;
              const depthTranslation = interpolate(
                frame,
                [25, 40, 48],
                [0, (col - 2) * -20, (col - 2) * -20],
                {
                  extrapolateLeft: "clamp",
                  extrapolateRight: "clamp"
                }
              );

              // Calculate current position in grid
              const currentRow = Math.floor(index / 5);
              const currentCol = index % 5;

              // Target position (13th circle - index 12, which is row 2, col 2)
              const targetRow = 2;
              const targetCol = 2;

              // Calculate movement to center (13th position) - delayed to start at frame 80
              const moveToCenter = interpolate(
                frame,
                [80, 85],
                [0, 1],
                {
                  extrapolateLeft: "clamp",
                  extrapolateRight: "clamp"
                }
              );

              // Calculate translation needed to move to center position
              let translateX = moveToCenter * (targetCol - currentCol) * (circleSize + Math.min(width * 0.02, 20));
              let translateY = moveToCenter * (targetRow - currentRow) * (circleSize + Math.min(width * 0.02, 20));

              // Special handling for WhatsApp circle (index 20) - MOVED BEFORE FIRST USE
              const isWhatsApp = index === 20;
              const gatheringComplete = frame >= 85;

              // Lock WhatsApp position at center when it starts disappearing
              if (isWhatsApp && frame >= 85) {
                translateX = 1 * (targetCol - currentCol) * (circleSize + Math.min(width * 0.02, 20));
                translateY = 1 * (targetRow - currentRow) * (circleSize + Math.min(width * 0.02, 20));
              }

              // Fade out all non-WhatsApp circles after gathering is complete
              let finalOpacity = isWhatsApp ? iconOpacity : interpolate(
                frame,
                [85, 90],
                [iconOpacity, 0],
                {
                  extrapolateLeft: "clamp",
                  extrapolateRight: "clamp"
                }
              );

              // NEW: Make WhatsApp circle (white circle) scale down and disappear during image animation
              if (isWhatsApp && frame >= 90) {
                finalOpacity = interpolate(
                  frame,
                  [90, 95],
                  [iconOpacity, 0],
                  {
                    extrapolateLeft: "clamp",
                    extrapolateRight: "clamp"
                  }
                );
              }

              // NEW: Scale down the WhatsApp circle during image animation
              let finalScale = iconScale;
              if (isWhatsApp && frame >= 100) {
                const whatsAppScaleDown = interpolate(
                  frame,
                  [85, 90],
                  [1, 0],
                  {
                    extrapolateLeft: "clamp",
                    extrapolateRight: "clamp"
                  }
                );
                finalScale = iconScale * whatsAppScaleDown;
              }

              // WhatsApp circle becomes plain white after gathering
              const circleColor = isWhatsApp && gatheringComplete ? "white" : item.color;
              const iconColor = isWhatsApp && gatheringComplete ? "white" : item.color;
              const backgroundColor = isWhatsApp && gatheringComplete ? "white" : "transparent";

              return (
                <div
                  key={index}
                  style={{
                    width: `${circleSize}px`,
                    height: `${circleSize}px`,
                    borderRadius: "50%",
                    backgroundColor: backgroundColor,
                    border: `${borderWidth}px solid ${circleColor}`,
                    boxShadow: `0 0 20px ${circleColor}40, inset 0 0 20px ${circleColor}20`,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    opacity: finalOpacity,
                    transform: `scale(${finalScale}) translateZ(${depthTranslation}px) translate(${translateX}px, ${translateY}px)`,
                    transition: "all 0.3s ease"
                  }}
                >
                  <window.IconifyIcon
                    icon={item.icon}
                    style={{
                      fontSize: `${circleSize * 0.4}px`,
                      color: iconColor
                    }}
                  />
                </div>
              );
            })}
          </div>
        </div>
      )}

      {/* New centered image with zoom and rotation animation, then moves to left and scales down */}
      {frame >= 95 && (
        <div style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: `translate(-50%, -50%) translateX(${imageTranslateX}px) scale(${imageScale * imageScaleDown}) rotate(${imageRotation}deg)`,
          opacity: imageOpacity
        }}>
          <Img
            src="https://i.postimg.cc/RVLHxwpQ/Vfgh-D76o-TQ0c-T5-Gdro-MBt-FJRa-Y8dfd.png"
