    sparkleProgress,
    [0, 1],
    [0, scale * 2],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" }
  );

  const rotation = interpolate(
    frame - delay,
    [0, 60],
    [75, 225],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" }
