const {
AbsoluteFill,
interpolate,
useCurrentFrame,
spring,
useVideoConfig,
} = window.Remotion;

const { IconifyIcon } = window;

const ChatMessage = ({ text, isUser, delay }) => {
const frame = useCurrentFrame();
const progress = spring({ frame: frame - delay, fps: 30, config: { damping: 12, stiffness: 200 } });

return (
  <div
    style={{
      display: "flex",
      justifyContent: isUser ? "flex-end" : "flex-start",
      opacity: progress,
      transform: `translateY(${interpolate(progress, [0, 1], [20, 0])}px)`,
      marginBottom: 24,
    }}
  >
    <div
      style={{
        maxWidth: "80%",
        padding: "16px 20px",
        borderRadius: 20,
        background: isUser ? "#007AFF" : "#E9ECEF",
        color: isUser ? "white" : "#212529",
        fontFamily: "sans-serif",
        fontSize: 16,
        boxShadow: "0 2px 8px rgba(0, 0, 0, 0.05)",
        lineHeight: 1.5,
      }}
    >
      {text}
    </div>
  </div>
);
};

const InputBar = ({ opacity }) => {
