return (
  <div
    style={{
      minHeight: 100,
      background: "white",
      borderRadius: 20,
      display: "flex",
      flexDirection: "column",
      padding: 16,
      opacity,
      transform: `translateY(${interpolate(opacity, [0, 1], [20, 0])}px)`,
      boxShadow: "0 4px 24px rgba(0, 0, 0, 0.1)",
      border: "1px solid #E5E5E5",
      position: "relative",
    }}
  >
    <div
      style={{
        flex: 1,
        color: "#212529",
        fontFamily: "sans-serif",
        fontSize: 16,
        lineHeight: 1.5,
        display: "flex",
        alignItems: "flex-start",
        paddingTop: 4,
      }}
    >
      <span>{text.slice(0, charCount)}</span>
    </div>
    <div
      style={{
        display: "flex",
        justifyContent: "flex-end",
        marginTop: 8,
      }}
    >
      <button
        style={{
          background: "#000000",
