const frame = useCurrentFrame();
const progress = interpolate(frame - delay, [0, 60], [0, percentage], { extrapolateRight: "clamp" });

return (
  <div style={{ width: "100%", height: "8px", background: "#F3F4F6", borderRadius: "4px", overflow: "hidden" }}>
    <div style={{
      width: `${progress}%`,
      height: "100%",
      background: color,
      borderRadius: "4px"
    }} />
  </div>
);
};

